mapper.select:
select * from club C left join member M on c.club_master = m.member_code left join club_category CC on c.club_code=cc.club_code
CC.club_cate like #{keyWord}
and club_location = #{clubLocation}

select * from club C left join member M on C.club_master=M.member_code 

	club_title like #{keyWord} 


		and club_location = #{clubLocation}

select member_code 
from club_members
where club_grade in('member','nm') and club_code = #{clubCode}

select * from club_boardlist
where club_code = #{clubCode}
order by club_boardlist_no

select count(*) as cnt,C.club_code from club_members C left join member M on C.member_code = M.member_code where C.club_code in
#{item}
and M.member_gender='m' group by C.club_code

select C.club_code,M.profile_age from club_members C left join member_profile M on C.member_code = M.member_code where c.club_code in
#{item}
order by c.club_code
